function _toConsumableArray(a) {
  if (Array.isArray(a)) {
    for (var b = 0, c = Array(a.length); b < a.length; b++) c[b] = a[b];
    return c;
  }
  return Array.from(a);
}

let playing = !0;

const timer = function () {
  return setInterval(() => {
    const a = document.getElementById('counter');
    const b = parseInt(a.innerText);
    a.innerText = b + 1;
  }, 1e3);
};

let interval = timer();
const minus = document.getElementById('-');
const plus = document.getElementById('+');
const heart = document.getElementById('<3');
const pause = document.getElementById('pause');
const commentForm = document.getElementsByTagName('form')[0];

minus.addEventListener('click', () => {
  const a = document.getElementById('counter');
  const b = parseInt(a.innerText);
  a.innerText = b - 1;
}),
plus.addEventListener('click', () => {
  const a = document.getElementById('counter');
  const b = parseInt(a.innerText);
  a.innerText = b + 1;
}),
heart.addEventListener('click', () => {
  const a = document.getElementById('counter');
  const b = parseInt(a.innerText);
  const c = document.querySelector('.likes');
  let d = void 0;
  if (
    []
      .concat(_toConsumableArray(c.children))
      .map((a) => parseInt(a.dataset.num))
      .includes(b)
  ) {
    d = document.querySelector(`[data-num="${b}"]`);
    const e = parseInt(d.children[0].innerText);
    d.innerHTML = `${b} has been liked <span>${e + 1}</span> times`;
  } else {
    (d = document.createElement('li')).setAttribute('data-num', b),
    (d.innerHTML = `${b} has been liked <span>1</span> time`),
    c.appendChild(d);
  }
}),
pause.addEventListener('click', function () {
  playing
    ? ((playing = !1), clearInterval(interval), (this.innerText = 'resume'))
    : ((playing = !0), (interval = timer()), (this.innerText = 'pause')),
  []
    .concat(_toConsumableArray(document.getElementsByTagName('button')))
    .forEach((a) => {
      a.id !== 'pause' && (a.disabled = !playing);
    });
}),
commentForm.addEventListener('submit', function (a) {
  a.preventDefault();
  const b = this.children[0];
  const c = b.value;
  b.value = '';
  const d = document.querySelector('.comments');
  const e = document.createElement('p');
  (e.innerText = c), d.appendChild(e);
});
